import {
    FORM_PROJECT,
    FIND_PROJECT,
    ADD_PROJECT
} from "../../types";


export default (state, action) => {
    switch (action.type) {
        case FORM_PROJECT:
            return {
                ...state,
                newProject: true
            };
        case FIND_PROJECT:
            return {
                ...state,
                projects: action.payload
            }
        default:
            return state;
    }
};
