import React, { Fragment, useContext } from "react";
import projectContext from "../../context/projects/projectContext";

import Task from "./Task";

const TasksList = () => {
    const projectsContext = useContext(projectContext);
    const { project, deleteProject } = projectsContext;

    if (!project) return <h2>Seleccione un proyecto</h2>;

    const [actualProject] = project;

    const tasksProject = [{ name: 'Elegir Plataforma', state: true, projectId: 1 },
    { name: 'Elegir colores', state: false, projectId: 2 },
    { name: 'Elegir Plataforma de pago', state: false, projectId: 3 },
    { name: 'Elegir Hosting', state: true, projectId: 4 },];

    const onClickDel = () => {
        deleteProject(actualProject.id);
    };
    return (
        <Fragment>
            <h2>Proyecto {actualProject.name}</h2>

            <ul className="listado-tareas">
                {tasksProject.length === 0 ? (
                    <li className="tarea">
                        <p>No Hay Tareas</p>
                    </li>
                ) : (
                        tasksProject.map(task => <Task task={task} />)
                    )}
            </ul>
            <button type="button" className="btn  btn-eliminar" onClick={onClickDel}>
                Eliminar Proyecto &times;
      </button>
        </Fragment>
    );
};

export default TasksList;
