import React, { useReducer } from 'react';
import TaskContext from './taskContext';
import taskReducer from './taskReducer';

import {
    TASKS_PROJECT,
    ADD_TASK,
    ERROR_TASK
} from '../../types/index';

const TaskState = props => {
    const initialState = {
        tasks: [
            { name: 'Elegir Plataforma', state: true, projectId: 1 },
            { name: 'Elegir colores', state: false, projectId: 2 },
            { name: 'Elegir colores', state: false, projectId: 3 },
            { name: 'Elegir Hosting', state: true, projectId: 4 },
            { name: 'Elegir Hosting', state: true, projectId: 1 },
            { name: 'Elegir Hosting', state: false, projectId: 2 },
            { name: 'Elegir Hosting', state: false, projectId: 3 },
            { name: 'Elegir Plataforma', state: true, projectId: 4 },
            { name: 'Elegir colores', state: false, projectId: 1 },
            { name: 'Elegir Plataforma de pago', state: false, projectId: 2 },
            { name: 'Elegir Plataforma', state: true, projectId: 3 },
            { name: 'Elegir colores', state: false, projectId: 4 },
            { name: 'Elegir Plataforma de pago', state: false, projectId: 3 },

        ],
        tasksProject: null,
        errorTask: false

    }

    const [state, dispatch] = useReducer(taskReducer, initialState);



    const findTasks = projectId => {
        dispatch({
            type: TASKS_PROJECT,
            payload: projectId

        })
    }

    const addTask = task => {
        dispatch({
            type: ADD_TASK,
            payload: task
        })
    }

    const errorTask = () => {
        dispatch({
            type: ERROR_TASK
        })
    }




    return (
        <TaskContext.Provider
            value={{
                tasks: state.tasks,
                tasksProject: state.tasksProject,
                errorTask: state.errorTask,
                findTasks,
                addTask
            }}
        >
            {props.children}
        </TaskContext.Provider>
    )
}

export default TaskState;
